% \usepackage{array} is required
\begin{tabularx}{\linewidth}{|X|>{\centering\arraybackslash}p{0.08\linewidth}|>{\centering\arraybackslash}p{0.08\linewidth}|>{\centering\arraybackslash}p{0.08\linewidth}|>{\centering\arraybackslash}p{0.08\linewidth}|>{\centering\arraybackslash}p{0.08\linewidth}|>{\centering\arraybackslash}p{0.08\linewidth}|>{\centering\arraybackslash}p{0.08\linewidth}|>{\centering\arraybackslash}p{0.08\linewidth}|}
\hline 
$m(kg)$ & 0 & 0,20 & 0,40 & 0,60 & 0,80 & 1,00 & 1,20 & 1,40 \\ 
\hline 
$x(cm)$ & 0 & 2,6 & 5,2 & 8,0 & 10,7 & 13,3 & 16,0 & 18,6 \\ 
\hline 
