\draw [->,line width = 3pt] (-4,-4) -- (-6,0);
\node  at (-4.5,-4.5) {\Large $\bm {O}$};
\node  at (-1.5,-4.5) {\Large $\bm {\overrightarrow{F_1}}$};
\node  at (-3,-2.5) {\Large $\bm {\overrightarrow{F_2}}$};
\node  at (-5.5,-2.5) {\Large $\bm {\overrightarrow{F_3}}$};
\node[right]  at (-1.5,-3.3) {\Large $\bm{33,7^o}$};
