					\draw[-,line width = 2pt] (-17.75,0.25) -- (-17.75,0.75);
					\draw  (-14.25,1.75) rectangle (-23.25,-2.25);
					\fill  (-22.25,0.75) rectangle (-23.25,1.75);
					\node[white] at (-22.75,1.25) {{\textbf{1}}} ;
					\draw[-,line width = 2pt] (-20.75,-1.25) -- (-20.75,-0.75);
					\node at (-20.75,-0.25) {\large{C}} ;
					\draw[-,line width = 2pt] (-20.25,-0.25) -- (-19.75,-0.25);
					\node at (-19.25,-0.25) {\large{N}} ;
					\draw[-,line width = 2pt] (-21.75,-0.25) -- (-21.25,-0.25);
					\draw[-,line width = 2pt] (-19.5,0) -- (-19,0);
					\node at (-20.75,1.25) {\large{H}} ;
					\node at (-19.25,-1.75) {\large{H}} ;
					\node at (-20.75,-1.75) {\large{H}} ;
					\draw[-,line width = 2pt] (-19.25,-1.25) -- (-19.25,-0.75);
					\draw[-,line width = 2pt] (-20.75,0.25) -- (-20.75,0.75);
					\end{tikzpicture}}
					\end{adjustbox}
					\end{center}
					\begin{enumerate}
					\item Donner la formule brute des molécules.
					\item Est-ce qu'il existe d'autres molécules
					ayant la même formule brute ?
					\end{enumerate}
					\end{enumerate}
					\end{exercice}%===  ===%

%Exercice 9
					\begin{exercice}{}/
					\begin{enumerate}
					\item Mendeleïev a classé les éléments chimiques dans le tableau périodique, selon l'ordre croissant de :
					\begin{multicols}{3}
					\begin{itemize}[label = $\square$]
						\item leurs masses molaires.
						\item Le numéro atomique Z.
						\item Le nombre des électrons. 
					\end{itemize}
					\end{multicols}
					\item Le tableau périodique actuelle classé les éléments chimiques selon la croissance de:
					\begin{multicols}{3}
					\begin{itemize}[label = $\square$]
						\item Nombre de neutrons N.
						\item Nombre de masse A 
						\item Numéro atomique Z.
					\end{itemize}
					\end{multicols}
					\item Dans un même groupe, les atomes des éléments chimiques ont :
					\begin{itemize}[label = $\square$]
						\item Les mêmes caractéristiques chimiques.
						\item Le même nombre de neutrons.
						\item Le même nombre d'électrons dans la couche externe.
					\end{itemize}
					\item Néon Ne (Z=10) est appartenant à les :
					\begin{multicols}{3}
					\begin{itemize}[label = $\square$]
					\item gaz rares 
					\item alcalins 
					\item halogènes
					\end{itemize}
					\end{multicols}
					\item L'atome de gaz rare :
					\begin{itemize}
						\item Peut céder un électron.
						\item Peut acquérir un électron.
						\item Ne donne pas un ion monoatomique.
					\end{itemize}
					\end{enumerate}
					\end{exercice}%===  ===%

%Exercice 1
					\begin{exercice}{}/
					\begin{enumerate}
