					\item $M(C) = 12\ g.mol^{-1}$
					\item $M(N) = 14\ g.mol^{-1}$
					\item $M(O) = 16\ g.mol^{-1}$
					\item $M(S) = 32\ g.mol^{-1}$
					\item $M(Fe) = 55,8\ g.mol^{-1}$
					\end{itemize}
					\end{multicols}
					\end{exercice}%===  ===%

