%Exercice 18
					\begin{exercice}{}/
\begin{enumerate}
\item Lors d'une désintégration $\beta^-$, il y a émission de:
\begin{multicols}{3}
\begin{itemize}[label = $\square$]
\item $He$
\item $_{1}^{0}e^{+}$
\item $_{-1}^{0}e^{-}$
\end{itemize}
\end{multicols}
\item l'émission de particules $_{1}^{0}e^{+}$ correspond à la radioactivité de type 
\begin{multicols}{3}
\begin{itemize}[label = $\square$]
\item $\alpha$
\item $\beta^{-}$
\item $\beta^{+}$
\end{itemize}
\end{multicols}
\item L'émission $\gamma$ est due à la dés-excitation 
\begin{multicols}{3}
\begin{itemize}[label = $\square$]
\item du noyau père. 
\item du noyau fils.
\item de la particule émise.
\end{itemize}
\end{multicols}
\item Lors d'une réaction nucléaire, il y a conservation : 
\begin{multicols}{3}
\begin{itemize}[label = $\square$]
\item de la masse.
\item du nombre de nucléons.
\item de Z.
\end{itemize}
\end{multicols} 
\item Le nombre moyen de noyaux radioactifs à un instant $t$ est donné par: 
\begin{multicols}{4}
\begin{itemize}[label = $\square$]
\item $N=N_0e^{\lambda t}$
\item $N=-N_0e^{\lambda t}$
\item $N=N_0e^{-\lambda t}$
\item $N=N_0e^{\frac{-t}{\tau}}$
\end{itemize}
\end{multicols}
\item Quelles sont les expressions de $\tau$ correctes? 
\begin{multicols}{5}
\begin{itemize}[label = $\square$]
\item $\tau = \dfrac{1}{\lambda}$
\item $\tau = \dfrac{ln(2)}{t_{\frac{1}{2}}}$
\item $\tau = \dfrac{ln(2)}{\lambda}$
\item $\tau = \dfrac{t_{\frac{1}{2}}}{ln(2)}$
\item $\tau = \dfrac{1}{t_{\frac{1}{2}}}$
\end{itemize}
\end{multicols}
\end{enumerate}
\end{exercice}%===  ===%

