\end{enumerate}
\textbf{Données :}
\begin{multicols}{2}
\begin{itemize}
\item $\lambda _{H_3O^{+}} = 3,5.10^{-2} S.m^{2}.mo1^{-1}$ 
\item $\lambda _{CH_3COO^{-}} = 4,1.10^{-3} S.m^{2}.mo1^{-1}$
\end{itemize}
\end{multicols}
\end{exercice}%=== source ===%

