Exercice.Solution:
%Solution 1
\begin{enumerate}
\item Une solution est :\\
\begin{minipage}{0.3\linewidth}
\begin{itemize}
\item Acide si : 
$pH < \dfrac{pK_e}{2}$
\item Neutre si :
$pH = \dfrac{pK_e}{2}$
\item Basique si :
$pH > \dfrac{pK_e}{2}$
\end{itemize}
\end{minipage}
\begin{minipage}{0.68\linewidth}
\begin{center}
\begin{tikzpicture}[scale = .75]
\shadedraw [left color=yellow,right color=orange] (-3.5,-2) rectangle (11.5,-1);
\draw[color = black, line width = 1pt] (11.5,-2.5)--(11.5,-0.5)--(13,-1.5)--(11.5,-2.5);
\fill[color = orange, line width = 1pt] (11.5,-2.5)--(11.5,-0.5)--(13,-1.5)--(11.5,-2.5);
\node at (-3.5,-0.2597) {${0}$} ;
\node at (-3.5,-1) {${|}$} ;
\node at (10.5,-0.2597) {${pk_e}$} ;
\node at (4,-1) {${|}$} ;
\node at (10.5,-1) {${|}$} ;
\node[] at (-0.5,-2.733) {${[H_3O^{+}] > [HO^{-}]}$} ;
\node[] at (-0.5,-0.2597) {${{pH < \frac{pK_e}{2}}}$} ;
\node[] at (8.5874,-2.733) {${[H_3O^{+}] < [HO^{-}]}$} ;
\node[] at (8.5874,-0.2597) {${{pH >\frac{pK_e}{2}}}$} ;
\node[] at (4,-2.733) {${[H_3O^{+}] = [HO^{-}]}$} ;
\node[] at (4,-0.2597) {${{pH = \frac{pK_e}{2}}}$} ;
\node[] at (-0.5,-1.5) {Acide} ;
\node[] at (8.5874,-1.5) {Base} ;
\node[] at (4,-1.5) {Neutre} ;
\node at (13,-0.5) {${pH}$} ;
\end{tikzpicture}
\end{center}
\end{minipage}
\\Le pH correspondant à la neutralité à cette température :
\begin{eqnarray*}
pH &=& \dfrac{pK_e}{2} \\
&=& \dfrac{-log(k_e)}{2} \\
&=& \dfrac{-log(2,4.10^{-14})}{2} \\
pH &=& 6.81 
\end{eqnarray*}
\item la salive est basique car $pH =6,85 > \dfrac{pK_e}{2}$. 
\end{enumerate}
					%===  ===%

%Solution 2
\begin{enumerate}
\item  Le $pK_e$, de l'eau à $0 ^{o}C$.
\begin{eqnarray*}
pK_e &=& -log(k_e)\\
&=& -log(1,1.10^{-15}) \\
pK_e &=& 14,9 
\end{eqnarray*}
\item Le pH correspondant à la neutralité à $0 ^{o}C$?
\begin{eqnarray*}
pH &=& \dfrac{pK_e}{2} \\
&=& \dfrac{14,9}{2}  \\
pH &=& 7,45 
\end{eqnarray*}
\item La
concentration des ions $HO^{-}$ dans cette solution :
\begin{eqnarray*}
k_e = \left[H_3O^{+}\right].\left[HO^{-}\right] \hspace{1cm}\Leftrightarrow \hspace{1cm} \left[HO^{-}\right] &=& \dfrac{k_e}{\left[H_3O^{+}\right]} \\
&=& \dfrac{k_e}{10^{-pH}} \\
&=& \dfrac{1,1.10^{-15}}{10^{-12,4}} \\
\left[ HO^{-}\right] &=& 2,76.10^{-3}\ mol.L^{-1}
\end{eqnarray*}
\end{enumerate}
					%===  ===%

%Solution 3
\begin{enumerate}
\item  Le pH correspondant à la neutralité à cette température :
\begin{eqnarray*}
pH &=& \dfrac{pK_e}{2} \\
&=& \dfrac{13,3}{2} \\
pH &=& 6,65 
\end{eqnarray*}
\item La solution aqueuse de $pH = 6,9$ est basique à $50 ^{o}C$ car :
$$pH =6,9 > \dfrac{pK_e}{2}$$
\end{enumerate}
					%===  ===%

%Solution 4
\begin{enumerate}
\item  L'équation de la réaction qui se produit lors de la mise en solution de l'acide méthanoique dans l'eau.
\begin{itemize}
\item Couples et réactifs :
\begin{center}
$HCO_2H/HCO_2^{-}$ et $H_3O^{+}/H_2O$
\end{center}
\item Demi-équations et équation bilan
\begin{eqnarray*}
HCO_2H &\rightleftarrows& HCO_2^{-} + H^{+} \\
H_2O + H^{+} &\rightleftarrows& H_3O^{+} \\
-------&----&------- \\
HCO_2H + H_2O &\rightleftarrows& HCO_2^{-} + H_3O^{+}
\end{eqnarray*}
\end{itemize}
\item Les concentrations des ions oxonium, des ions méthanoate et de l'acide méthanoique dans cette
solution.
\vspace{2mm}\\\begin{tabularx}{\linewidth}{|X|>{\centering\arraybackslash}Xcc>{\centering\arraybackslash}Xcc>{\centering\arraybackslash}Xcc>{\centering\arraybackslash}X|}
\hline
Équation     & $HCO_2H$                      & \multicolumn{2}{c}{+}                         & $H_2O$                     & \multicolumn{2}{c}{$\rightleftarrows$}                         & $HCO_2^-$                  & \multicolumn{2}{c}{+}                         & \multicolumn{1}{c|}{$H_3O^+$} \\ \hline
État initial & \multicolumn{1}{c}{$CV$}     & \multicolumn{1}{c|}{} & \multicolumn{1}{c}{} & \multicolumn{1}{c}{excès} & \multicolumn{1}{c||}{} & \multicolumn{1}{c}{} & \multicolumn{1}{c}{0}     & \multicolumn{1}{c|}{} &                       & 0                             \\ \hline
État final   & \multicolumn{1}{c}{$CV-x_f$} & \multicolumn{1}{c|}{} & \multicolumn{1}{c}{} & \multicolumn{1}{c}{excès} & \multicolumn{1}{c||}{} & \multicolumn{1}{c}{} & \multicolumn{1}{c}{$x_f$} & \multicolumn{1}{c|}{} & \multicolumn{1}{c}{} & \multicolumn{1}{c|}{$x_f$}    \\ \hline
\end{tabularx}
D'après le tableau d'avancement :
\begin{itemize}
\item Les concentrations des ions oxonium $\left[H_3O^{+}\right]$, des ions méthanoate $\left[HCO_2^{-}\right]$.
\begin{eqnarray*}
\left[HCO_2^{-}\right] = \left[H_3O^{+}\right] &=& 10^{-pH}
= 10^{-2,65} = 2,24.10^{-3}\ mol.L^{-1}
\end{eqnarray*}
\item Concentration de l'acide méthanoique $\left[HCO_2H\right]$.
\begin{eqnarray*}
\left[HCO_2H\right] &=& \dfrac{CV - x_f}{V} \\
&=& \dfrac{CV - \left[H_3O^{+}\right].V}{V} \\
&=& \dfrac{\left(C - \left[H_3O^{+}\right]\right).\cancel{V}}{\cancel{V}} \\
&=& C - \left[H_3O^{+}\right] \\
&=& 3.10^{-2} - 2,24.10^{-3} \\
\left[HCO_2H\right] &=&  2,78.10^{-2}\ mol.L^{-1}
\end{eqnarray*}
\end{itemize}
\item En déduire les valeurs de la constante d'acidité $k_A$ et du $pk_A$ du couple considéré à $25 ^{o}C$.
\begin{itemize}
\item la constante d'acidité $k_A$ :
\begin{eqnarray*}
k_A &=& \dfrac{\left[HCO_2^{-}\right]_{(\text{éq})}.\left[H_3O^{+}\right]_{(\text{éq})}}{\left[HCO_2H\right]_{(\text{éq})}} \\
&=& \dfrac{\left[H_3O^{+}\right]^{2}_{(\text{éq})}}{\left[HCO_2H\right]_{(\text{éq})}} \\
&=& \dfrac{\left(2,24.10^{-3}\right)^{2}}{2,78.10^{-2}} \\
k_A &=& 1,80.10^{-4}
\end{eqnarray*}
\item $pk_A$ de ce couple :
\begin{eqnarray*}
pk_A &=& -log(k_A) \\
&=& -log(1,80.10^{-4}) \\
pk_A &=& 3,74
\end{eqnarray*}
\end{itemize}
\end{enumerate}
					%===  ===%

Exercice.Enonce:
%Exercice 5
					\begin{exercice}{}/
\begin{enumerate}
\item Écrire les équations de réaction entre :
\begin{enumerate}
\item l'acide lactique $CH_3CHOHCO_2H$ et l'ion nitrite $NO_2^-$.
\item L'acide formique $HCO_2H$ et l'ion hydroxyde $HO^{-}$.
\end{enumerate}
\item Calculer la constante d'équilibre associée à chacune de ces réactions à $25 ^{o}C$.
\end{enumerate}
\textbf{Données à $25 ^{o}C$:} 
%\begin{multicols}{2}
\begin{itemize}
\item $pk_A(CH_3CHOHCO_2H/ CH_3CHOHCO_2^{-})= 3,9$
\item $pk_A(HNO_2/ NO_2^{-}) = 3,3$
\item $pk_A(H_2O/ HO^{-}) = 14,0$
\item $pk_A(HCO_2H/HCO_2^{-})= 3,8$
\end{itemize}
%\end{multicols}
\end{exercice}%=== source ===%

Exercice.Solution:
%Solution 5
\begin{enumerate}
\item Les équations de réaction entre :
\begin{enumerate}
\item l'acide lactique $CH_3CHOCH_3CHOHCO_2H$ et l'ion nitrite $NO_2^-$.
\begin{itemize}
\item Couples et réactifs :
\begin{center}
$CH_3CHOHCO_2H/CH_3CHOHCO_2^{-}$ et $NO_2H/NO_2^-$
\end{center}
\item Demi-équations et équation bilan
\begin{eqnarray*}
CH_3CHOHCO_2H &\rightleftarrows& CH_3CHOHCO_2^{-} + H^{+} \\
NO_2^- + H^{+} &\rightleftarrows& NO_2H \\
-------&----&------- \\
CH_3CHOHCO_2H + NO_2^- &\rightleftarrows& CH_3CHOHCO_2^{-} + NO_2H
\end{eqnarray*}
\end{itemize}
\item L'acide formique $CH_3CHOHCO_2H$ et l'ion hydroxyde $HO^{-}$.
\begin{itemize}
\item Couples et réactifs :
\begin{center}
$HCO_2H/HCO_2^{-}$ et $H_2O/HO^{-}$
\end{center}
\item Demi-équations et équation bilan
\begin{eqnarray*}
HCO_2H &\rightleftarrows& HCO_2^{-} + H^{+} \\
HO^{-} + H^{+} &\rightleftarrows& H_2O \\
-------&----&------- \\
HCO_2H + HO^{-} &\rightleftarrows& HCO_2^{-} + H_2O
\end{eqnarray*}
\end{itemize}
\end{enumerate}
\item La constante d'équilibre associée à chacune de ces réactions à $25 ^{o}C$.
\begin{itemize}
\item En général la constante d'équilibre $K$ associée à la réaction entre l'acide $HA_1$ du couple $HA_1/A_1^{-}$ de constante d'acidité $K_{A_1}$ et la base $A_2^{-}$ du couple $HA_2/A_2^{-}$ de constante d'acidité $K_{A_2}$.
$$HA_1 + A_2^{-} \rightleftarrows A_1^{-} + HA_2$$
\begin{eqnarray*}
K &=& \dfrac{[A_1^{-}]_{\text{éq}}.[HA_2]_{\text{éq}}}{[HA_1]_{\text{éq}}.[A_2^{-}]_{\text{éq}}} \\
&=& \dfrac{[A_1^{-}]_{\text{éq}}.[HA_2]_{\text{éq}}.[H_3O^{+}]_{\text{éq}}}{[HA_1]_{\text{éq}}.[A_2^{-}]_{\text{éq}}.[H_3O^{+}]_{\text{éq}}} \\
&=& \dfrac{[A_1^{-}]_{\text{éq}}.[H_3O^{+}]_{\text{éq}}}{[HA_1]_{\text{éq}}}.\dfrac{[HA_2]_{\text{éq}}}{[A_2^{-}]_{\text{éq}}.[H_3O^{+}]_{\text{éq}}} \\
&=& \dfrac{\dfrac{[A_1^{-}]_{\text{éq}}.[H_3O^{+}]_{\text{éq}}}{[HA_1]_{\text{éq}}}}{\dfrac{[A_2^{-}]_{\text{éq}}.[H_3O^{+}]_{\text{éq}}}{[HA_2]_{\text{éq}}}} \\
&=& \dfrac{K_1}{K_2} \\
&=& \dfrac{10^{-pK_1}}{10^{-pK_2}} \\
K &=& 10^{pK_2 -pK_1}
\end{eqnarray*}
\item La constante d'équilibre associée à la réaction entre l'acide lactique $CH_3CHOCH_3CHOHCO_2H$ et l'ion nitrite $NO_2^-$.
\begin{eqnarray*}
K &=& 10^{pk_A(HNO_2/ NO_2^{-}) - pk_A(CH_3CHOHCO_2H/ CH_3CHOHCO_2^{-})} \\
&=& 10^{3,3 - 3,9} \\
&=& 0,25
\end{eqnarray*}
\item La constante d'équilibre associée à la réaction entre l'acide formique $CH_3CHOHCO_2H$ et l'ion hydroxyde $HO^{-}$.
\begin{eqnarray*}
K &=& 10^{pk_A(H_2O/ HO^{-}) - pk_A(HCO_2H/HCO_2^{-})} \\
&=& 10^{14 - 3,8} \\
&=& 1,58.10^{10}
\end{eqnarray*}
\end{itemize}
\end{enumerate}
					%===  ===%

%Solution 6
\begin{enumerate}
\item  L'espèce prédominante du couple $HA/A^{-}$ dans l'estomac, dans le duodénum et dans le sang.
\begin{center}
\begin{tikzpicture}[scale = .75]
\draw[->,color = black, line width = 1pt] (-2.5,0)--(10,0);
\node at (10,0.5) {${pH}$} ;
\node at (3.5,0.5) {${pK_A = 3,5}$} ;
\node at (3.5,0) {$|$} ;
\node at (0.5,-0.5) {$[HA] >[A^-]$} ;
\node at (7,-0.5) {$ [A^-]>[HA]$} ;
\node at (-2.5,0.5) {${0}$} ;
\node at (-2.5,0) {$|$} ;
\end{tikzpicture}
\end{center}
\begin{itemize}
\item Dans l'estomac : $pH = 1,5 < pK_A$ L'espèce prédominante est l'acide $HA$.
\item Au niveau du duodénum : $pH = 6,0 > pK_A$ L'espèce prédominante est la base $A^{-}$.
\item Dans le sang : $pH = 7,4 > pK_A$ L'espèce prédominante est la base $A^{-}$.
\end{itemize}
\item Le rapport $\dfrac{[A^{-}]}{[HA]}$ dans l'estomac.
\begin{eqnarray*}
pH = pK_A + log\left(\dfrac{[A^{-}]}{[HA]}\right) \Leftrightarrow log\left(\dfrac{[A^{-}]}{[HA]} \right) &=& pH - pK_A \\
10^{log\left(\dfrac{[A^{-}]}{[HA]} \right)} &=& 10^{pH - pK_A} \\
\dfrac{[A^{-}]}{[HA]} &=& 10^{1,5 - 3,5} \\
&=& 0,01 \\
\dfrac{[A^{-}]}{[HA]} &=& 1\%
\end{eqnarray*}
\end{enumerate}
					%===  ===%

