%Exercice 10
\begin{exercice}{}/
نعتبر النتائج التجريبية جانبه :
\begin{enumerate}
\item أجرد القوى المطبقة على الجسم
$(S)$
في الحالة 1
واستنتج قيمة كتلته.

\vspace{-0.4 cm}
\begin{minipage}{0.64\linewidth}
\item أجرد القوى المطبقة على الجسم
$(S)$
عندما يكون
مغمورا في السائل.
\item أحسب قيمة شدة دافعة أرخميدس المطبقة من طرف كل
سائل على
$(S)$.
\item باعتمادك على الحالة 3،
 أحسب
 $V$
 حجم الجسم
$(S)$.
\item أحسب الكتلة الحجمية للكحول و للماء المالح.
\end{minipage}
\begin{minipage}{0.35\linewidth}
\begin{flushleft}
\fbox{\includegraphics[width=\linewidth]{images/Tc/Ar/p5/im2.png}}
\end{flushleft}
\end{minipage}
\end{enumerate}
\textbf{نعطي :}
$\rho _{eau} = 1\ g.cm^{-3}$
و
$g = 10\ N.kg^{-1}$
\end{exercice}%=== source ===%

%Exercice 1
\begin{exercice}{}/
\begin{minipage}{0.5\linewidth}
يمثل الشكل أسفله مجموعة من متجهات قوى ممثلة في
المعلم
$(O,\overrightarrow{i},\overrightarrow{j})$.

\begin{enumerate}
\item حدد إحداثيات كل متجهة في المعلم
$R(O,\overrightarrow{i},\overrightarrow{j})$.
\item حدد منظم كل متجهة.
\item حدد قيمة الزاويتين
$\alpha$
و
$\varphi$.
\item حدد اتجاه ومنحى ومنظم كل متجهة.
\item احسب المجموع المتجهي لكل القوى
$\sum^{n = 6}_{i=1} \overrightarrow{F}_i$.
\end{enumerate}
\end{minipage}
\begin{minipage}{0.49\linewidth}
\begin{flushleft}
\fbox{\includegraphics[width=0.9\linewidth]{images/Tc/Ar/p6/im9.png}}
\end{flushleft}
\end{minipage}
\end{exercice}%=== source ===%

%Exercice 4 =====================================
\begin{exercice}{}/
\begin{minipage}{0.69\linewidth}
نعتبر جسما صلبا
$(S)$
كتلته
$m=300kg$
 في
توازن حيث يكون الخيطان
الزاويتين
$\beta = 30^\circ$
 و
 $\alpha = 45^\circ$
وكتلتاهما
مهملة.
\begin{enumerate}
\item أجرد القوى المطبقة
على الجسم 
$(S)$.
\item مثل الخط المضلعي بالسلم
$1cm \rightarrow 10^{3}N$.
\item أوجد شدات القوى. 
\textbf{نعطي :}
$g=10\ N.kg^{-1}$.
\end{enumerate}
\end{minipage}
