%Exercice 3
\begin{exercice}{}/
					نعتبرالتركيب التالي،كل ثنائيات القطب المستعملة فيه موصلات أومية.\\
					\hspace{1cm} $R_1=200 \Omega$ \hspace{1cm};\hspace{1cm}
					$R_2=300 \Omega$ \hspace{1cm};\hspace{1cm}
					$R_3=100 \Omega$ \hspace{1cm};\hspace{1cm}
					$R_4=400 \Omega$   \\
					\begin{center}
					\usetikzlibrary {circuits.ee.IEC}
					\fbox{
					\resizebox{3cm}{!}{
					\begin{tikzpicture}[circuit ee IEC]
      \draw (0,0) to [resistor={info=$R_1$},current direction={pos=.8,info=$I_1$,color=red}](2,2);
      \draw (2,2) to [resistor={info=$R_2$}] (4,0);
      \draw (0,0) to [resistor={info'=$R_4$},current direction={pos=.8,info=$I_2$,color=red}] (2,-2);
  	\draw (2,-2) to [resistor={info'=$R_3$}] (4,0);
  	\draw (-1,0) to [current direction={pos=.5,info=$I$,color=red}] (0,0);
  	\draw (4,0)  --(5,0);
      \node [contact] at (2,2) {};
      \node [contact] at (2,-2) {};
      \node [contact] at (4,0) {};
      \node [contact] at (0,0) {};
      \node [contact] at (-1,0) {};
      \node [contact] at (5,0) {};
      \node at (2,2.5) {A};
      \node at (2,-2.5) {B};
      \node at (4,0.5) {D};
      \node at (0,0.5) {C};
	\end{tikzpicture}}}
	\end{center}
					\begin{enumerate}
					\item ‏أحسب مقاومه الموصل الأومي المكافئ 
					$(C,D‏)$ .
					\item في حالة
					$I=0.2A$ ‏
					، أحسب التوتر 
					$U_{CD}$‏.
					أحسب
					 $I_1$
					  ‏  و 
					 $I_2$
					   ‏ ،استنتج التوتر
					$U_{AB}$‏. 
					\item 
					نربط النقطتين $A$  ‏و $B$  ‏بسلك توصيل ذي مقاومة مهملة. 
					\begin{enumerate}
					\item أحسب قيمة شدة التيار في كل فرع. 
					\item ‏أحسب المقاومة المكافئة بين
					$C$
					 و
					 $D$
					  ‏والتوتر
					 $U_{CD}$‏. 
					\end{enumerate}
					\end{enumerate}					
					\end{exercice}%=== source ===%

