  \begin{center}
    \begin{tikzpicture}[circuit ee IEC]
      \draw (4.2,0) to [Zener diode={info=$D_2$},current direction={pos=-0.8,color={red}}] (4.2,-2.4);
      \node at (-2,0.2) {$P$};
