%Exercice 5
					\begin{exercice}{}/
					تمثل الصورة التالية نموذجين جزيئيين :
\\\begin{minipage}{0.6\linewidth}	
\indent 	
\begin{itemize}
\item 	
				الشكل (أ) : النموذج الجزيئي للميثوكسي ميثان، وهو غاز يستعمل في الصناعة البترولية  للتقليل من الإنبعاثات الملوثة.
\item					
					الشكل (ب) : النموذج الجزيئي للإيثانول وهو كحول (سائل).					
\end{itemize}
\end{minipage}	
\begin{minipage}{0.4\linewidth}
\begin{flushleft}
\fbox{\includegraphics[width=0.9\linewidth]{images/Tc/Ar/c5/im10.png}}
\end{flushleft}
\end{minipage}		
					\begin{enumerate}
					\item أكتب الصيغ المنشورة والنصف منشورة والإجمالية لكل جزيئة.
					\item بين أن الجزيئتين متماكبتان.
					\item هل الجزيئتان تمثلان نفس النوع الكيميائي؟ علل جوابك.
					\end{enumerate}
					\end{exercice}%===  ===%

%Exercice 6
					\begin{exercice}{}/
					نعتبر المركبات التالية :
					\begin{itemize}
					\item  الماء الأوكسجيني أو بيروكسيد الهيدروجين يستعمل كمطهر ويتكون من الجزيئات ذات الصيغة الإجمالية
				$H_2O_2$.	
					\item غاز البروبن وهو أحد مشتقات البترول، وصيغته الإجمالية هي  :
					$C_3H_6$.
					\item غاز الأستيلين ويستعمل في اللحام، وصيغته الإجمالية هي : 
					$C_2H_2$.
					\item رباعي كلوروميثان وهو مذيب عضوي، وصيغته الإجمالية هي :
					$CCl_4$.
					\end{itemize}
					\begin{enumerate}
					\item  مثل كلا من هذه الجزيئات حسب نموذج لويس.
					\item أكتب الصيغة المنشورة لكل من هذه الجزيئات.
					\end{enumerate}
					\textbf{نعطي :}
					\begin{multicols}{4}
					\begin{itemize}
					\item $_{1}H$
					\item $_{6}C$
					\item $_{8}O$
					\item $_{17}Cl$
					\end{itemize}
					\end{multicols}
					\end{exercice}%===  ===%

%Exercice 7
					\begin{exercice}{}/
					أحد أسباب التشنج العضلي تراكم الحمض اللبني (أو حمض اللكتيك) في العضلات.\\
\begin{minipage}{0.68\linewidth}				
					الشكل جانبه يمثل النموذج الجزيئي لهذا الحمض.
					\begin{enumerate}
					\item  أجرد الذرات المكونة لجزيئة حمض اللكتيك.
					\item أكتب الصيغة الإجمالية لجزيئة هذا الحمض.
					\item حدد عدد الروابط التي تقيمها كل ذرة.
					\item أكتب الصيغتين المنشورة والنصف منشورة لجزيئة حمض اللكتيك.
					\end{enumerate}
					\end{minipage}	
\begin{minipage}{0.3\linewidth}
\begin{flushleft}
