%Exercice 1
					\begin{exercice}{}/
					\renewcommand{\labelitemi}{$\square$}
					اختر الجواب أو الأجوبة الصحيحة :
					\begin{enumerate}
					\item يدور جسم صلب حول محور ثابت خلال مدة 
					$\Delta t$ :
					\begin{multicols}{3}
					\begin{itemize}
					\item تدور جميع نقطه بنفس الزاوية.
					\item تدور جميع نقطه بزوايا مختلفة.
					\item تقطع جميع نقطه نفس المسافة.
					\end{itemize}
					\end{multicols}
					\item تتعلق سرعة نقطة من جسم صلب في دوران حول محور ثابت 
					\begin{multicols}{3}
					\begin{itemize}
					\item بالمسافة بينها وبين محور الدوران فقط.
					\item بالسرعة الزاوية للجسم فقط.
					\item
					بهما معا.
					\end{itemize}
					\end{multicols}
					\item العلاقة بين السرعتين الخطية والزاوية هي :
					\begin{multicols}{3}
					\begin{itemize}
					\item $v=R.\omega$
					\item $\omega = R.v$
					\item $v=\dfrac{R}{\omega}$
					\end{itemize}
					\end{multicols}
					\item وحدة قيمة السرعة الزاوية هي :
					\begin{multicols}{3}
					\begin{itemize}
					\item $tr.s^{-1}$
					\item $tr.min^{-1}$
					\item $rad.s^{-1}$
					\end{itemize}
					\end{multicols}
					\item المعادلة الزمنية لحركة نقطة من جسم صلب في دوران منتظم حول محور ثابت هي :
					\begin{multicols}{2}
					\begin{itemize}
					\item $\theta = v.t+\theta_0$
					\item $s=v.t + s_0$
					\item $s=\omega .t+s_0$
					\item $\theta =\omega .t +\theta_0$
					\end{itemize}
					\end{multicols}
					\item الدور 
					$T$
					لحركة دوران منتظم :
					\begin{multicols}{3}
					\begin{itemize}
					\item يساوي مدة دورة واحدة.
					\item يساوي عدد الدورات في الثانية.
					\item يحقق العلاقة التالية :
					$T = \dfrac{2\pi.R}{v}$
					\end{itemize}
					\end{multicols}
					\end{enumerate}
					\renewcommand{\labelitemi}{$\bullet$}
					\end{exercice}%===  ===%

