%Exercice 1
					\begin{exercice}{}/
					إختر الجواب الصحيح.
					\begin{enumerate}
					\item  يحقق التوتر 
					$U$
					بين مربطي مولد العلاقة :
					\vspace{-0.5cm}
					\begin{tabbing}
					\hspace{4cm}\=\hspace{4cm}\=\kill
					$U=E-r.I\ \square$  \&gt  $U=E+r.I\ \square$ \&gt $U=EI-r.I^2\ \square$
					\end{tabbing} 
					\vspace{-0.5cm}
					\item تكتب الحصيلة الطاقية لمستقبل كما يلي :
					\vspace{-0.5cm}
					\begin{tabbing}
					\hspace{4cm}\=\hspace{4cm}\=\kill
					$W_J =W_e+W_U\ \square$  \&gt  $W_e =W_J+W_U\ \square$ \&gt $W_U =W_e+W_J\ \square$
					\end{tabbing} 
					\vspace{-0.5cm}
					\item الطاقة النافعة التي ينتجها مستقبل خلال مدة 
					$\Delta t$
					هي :
					\vspace{-0.5cm}
					\begin{tabbing}
					\hspace{4cm}\=\hspace{4cm}\=\kill
					$W =r.I^2.\Delta t\ \square$  \&gt  $W=U.I.\Delta t\ \square$ \&gt $W=E'.I.\Delta t\ \square$
					\end{tabbing} 
					\vspace{-0.5cm}
					\item مردود مولد كهربائي هو :
					\vspace{-0.5cm}
					\begin{tabbing}
					\hspace{4cm}\=\hspace{4cm}\=\kill
					$\rho = \dfrac{U_{PN}}{E}\ \square$  \&gt  $\rho = \dfrac{E'}{E}\ \square$ \&gt $\rho = \dfrac{E}{E'}\ \square$
					\end{tabbing} 
					\vspace{-0.5cm}
					\item مردود مستقبل كهربائي هو :
					\vspace{-0.5cm}
					\begin{tabbing}
					\hspace{4cm}\=\hspace{4cm}\=\kill
					$\rho = \dfrac{U_{AB}}{E'}\ \square$  \&gt  $\rho = \dfrac{E'}{E}\ \square$ \&gt $\rho = \dfrac{E'}{U_{AB}}\ \square$
					\end{tabbing} 
					\vspace{-0.5cm}
					\end{enumerate}
					\end{exercice}%=== مقرر مسار ===%

%Exercice 3
					\begin{exercice}{}/
					أنقل الشكل أسفله، واكتب تحت الأسهم أشكال الطاقة المنتقلة وتعبيرها.
					\begin{center}
					\fbox{\includegraphics[width=0.3\linewidth]{images/PremierBac/Ar/p11/im1.png}}
					\end{center}
					\end{exercice}%=== مقرر مسار ===%

%Exercice 9
					\begin{exercice}{}/
					نركب على التوالي مولدا 
					$(E = 6\ V;r=1,2\ \Omega)$
					ومحللا كهربائيا 
					$(E' = 2\ V;r'=10\ \Omega)$
					مدة الإشتغال هي عشرون دقيقة.
					\begin{center}
					\fbox{\includegraphics[width=0.4\linewidth]{images/PremierBac/Ar/p11/im2.png}}
					\end{center}
					\begin{enumerate}
					\item بين أن شدة التيار المار في الدارة هي  :
					$I=0,36\ A$،
					أن التوتر بين مربطي المحلل الكهربائي هو 
					$5,6\ V$.
					\item أحسب :
					\begin{enumerate}
					\item الطاقة الكهربائية الممنوحة من طرف المولد.
					\item الطاقة الكهربائية التي تحولت الى طاقة كيميائية من طرف المحلل الكهربائي.
					\item الطاقة الحرارية المبددة بمفعول جول في مجموع الدارة.
					\end{enumerate}
					\item ما قيمة :
					\begin{enumerate}
					\item مردود المحلل الكهربائي؟
					\item مردود المولد؟
					\item المردود الكلي للدارة؟
					\end{enumerate}
					\end{enumerate}
					\end{exercice}%=== مقرر مسار ===%

