%Exercice 1
					\begin{exercice}{}/
أنجز عملية التحويل الى الوحدة المطلوبة في كل من الحالات التالية :
\begin{enumerate}
\item الموصلية :
\textfrench{
\begin{itemize}
\item $\bm{{\sigma = 65,4\ mS.m^{-1} =}}$ \dotfill $\bm{{\ S.m^{-1}}}$
\item $\bm{{\sigma = 26,7.10^{-2}\ S.cm^{-1} = }}$ \dotfill $\bm{{\  S.m^{-1}}}$
\item $\bm{{\sigma = 65,4.10^{-1}\ \mu S.cm^{-1} = }}$ \dotfill $\bm{{\  S.m^{-1}}}$
\end{itemize}}
\item الموصلية المولية الأيونية :
\textfrench{
\begin{itemize}
\item $\bm{{\lambda = 78,5.10^{-2}\ S.m^{2}.mol^{-1} = }}$ \dotfill $\bm{{\  mS.m^{2}.mol^{-1}}}$
\item $\bm{{\lambda = 5,7.10^{3}\ \mu S.m^{2}.mol^{-1} = }}$ \dotfill $\bm{{\  S.m^{2}.mol^{-1}}}$
\item $\bm{{\lambda = 19,5\ mS.m^{2}.mol^{-1} = }}$ \dotfill $\bm{{\  S.m^{2}.mol^{-1}}}$
\end{itemize}
}
\item التركيز المولي :
\textfrench{
\begin{itemize}
\item $\bm{{C = 1,3.10^{-2}\ mol.L^{-1} = }}$ \dotfill $\bm{{\  mol.m^{-3}}}$
\item $\bm{{C = 78,5\ mmol.m^{-3} = }}$ \dotfill $\bm{{\  mol.L^{-1}}}$
\item $\bm{{C = 6,5.10^{-2}\ mmol.dm^{-3} = }}$ \dotfill $\bm{{\  mol.m^{-3}}}$
\end{itemize}}
\end{enumerate}
					\end{exercice}%===  ===%

Exercice.Solution:
%Solution 1
					\textbf{\begin{solution}{}/
عملية التحويل الى الوحدة المطلوبة في الحالة :
\begin{enumerate}[label=\protect\circled{\color{white}\textbf{\arabic*}}]
\item الموصلية :
\vspace{-0.3cm}
\textfrench{
\begin{itemize}
\item $\bm{{\sigma = 65,4\ mS.m^{-1} =}}$ \dotfill $\bm{{\ S.m^{-1}}}$
\begin{eqnarray*}
\sigma &=& 65,4\ \textcolor{red}{mS}.m^{-1}\hfill\\
&=& 65,4\ \textcolor{red}{10^{-3}S}.m^{-1} \\
&=& 65,4.10^{-3}\ S.m^{-1}
\end{eqnarray*}
\item $\bm{{\sigma = 26,7.10^{-2}\ S.cm^{-1} = }}$ \dotfill $\bm{{\  S.m^{-1}}}$ 
\begin{eqnarray*}
\sigma &=& 26,7.10^{-2}\ S.\textcolor{red}{cm}^{-1} \\
&=& 26,7.10^{-2} \ S.(\textcolor{red}{10^{-2}m})^{-1} \\
&=& 26,7.10^{-2} \ S.\textcolor{red}{10^{2}.m^{-1}} \\
&=& 26,7.10^{-2}.10^{2} \ S.m^{-1} \\
&=& 26,7\ S.m^{-1}
\end{eqnarray*}
\item $\bm{{\sigma = 65,4.10^{-1}\ \mu S.cm^{-1} = }}$ \dotfill $\bm{{\  S.m^{-1}}}$
\begin{eqnarray*}
\sigma &=& 65,4.10^{-1}\ \textcolor{red}{\mu S}.\textcolor{blue}{cm}^{-1} \\
&=& 65,4.10^{-1}\ \textcolor{red}{10^{-6}S}.(\textcolor{blue}{10^{-2}m})^{-1} \\
&=& 65,4.10^{-1}\ \textcolor{red}{10^{-6}S}.\textcolor{blue}{10^{2}.m^{-1}} \\
&=& 65,4.10^{-1}.10^{-6}.10^{2}\ S.m^{-1} \\
&=& 65,4.10^{-5}\ S.m^{-1}
\end{eqnarray*}
\vspace{-0.2cm}
\end{itemize}}
\vspace{-0.3cm}
\item الموصلية المولية الأيونية :
\vspace{-0.2cm}
\textfrench{
\begin{itemize}
\item $\bm{{\lambda = 78,5.10^{-2}\ S.m^{2}.mol^{-1} = }}$ \dotfill $\bm{{\  mS.m^{2}.mol^{-1}}}$ 
\begin{eqnarray*}
\lambda &=& 78,5.10^{-2}\ \textcolor{red}{S}.m^{2}.mol^{-1} \\
&=& 78,5.10^{-2}\ \textcolor{red}{10^{3}mS}.m^{2}.mol^{-1} \\
&=& 78,5.10^{-2}.10^{3}\ mS.m^{2}.mol^{-1} \\
&=& 78,5.10^{1}\ mS.m^{2}.mol^{-1} \\
&=& 785\ mS.m^{2}.mol^{-1}
\end{eqnarray*}
\item $\bm{{\lambda = 5,7.10^{3}\ \mu S.m^{2}.mol^{-1} = }}$ \dotfill $\bm{{\  S.m^{2}.mol^{-1}}}$ 
\begin{eqnarray*}
\lambda &=& 5,7.10^{3}\ \mu S.m^{2}.mol^{-1} \\
&=& 5,7.10^{3}\ \textcolor{red}{\mu S}.m^{2}.mol^{-1} \\
&=& 5,7.10^{3}\ \textcolor{red}{10^{-6} S}.m^{2}.mol^{-1} \\
&=& 5,7.10^{3}.10^{-6}\ S.m^{2}.mol^{-1} \\
&=& 5,7.10^{-3}\ S.m^{2}.mol^{-1}
\end{eqnarray*}
\item $\bm{{\lambda = 19,5\ mS.m^{2}.mol^{-1} = }}$ \dotfill $\bm{{\  S.m^{2}.mol^{-1}}}$ 
\begin{eqnarray*}
\lambda &=& 19,5\ \textcolor{red}{mS}.m^{2}.mol^{-1}\\
&=& 19,5\ \textcolor{red}{10^{-3}S}.m^{2}.mol^{-1}\\
&=& 19,5.10^{-3}\ S.m^{2}.mol^{-1}\\
\end{eqnarray*}
\vspace{-0.2cm}
\end{itemize}}
\vspace{-0.3cm}
\item التركيز المولي :
\vspace{-0.3cm}
\textfrench{
\begin{itemize}
\item $\bm{{C = 1,3.10^{-2}\ mol.L^{-1} = }}$ \dotfill $\bm{{\  mol.m^{-3}}}$
\begin{eqnarray*}
C &=& 1,3.10^{-2}\ mol.\textcolor{red}{L}^{-1} \\
&=& 1,3.10^{-2}\ mol.(\textcolor{red}{10^{-3}m^{3}})^{-1} \\
&=& 1,3.10^{-2}\ mol.\textcolor{red}{10^{3}.m^{-3}} \\
&=& 1,3.10^{-2}.10^{3}\ mol.m^{-3} \\
&=& 1,3.10^{-2}.10^{3}\ mol.m^{-3} \\ 
&=& 1,3.10^{1}\ mol.m^{-3} \\  
&=& 13\ mol.m^{-3} \\  
\end{eqnarray*}
\item $\bm{{C = 78,5\ mmol.m^{-3} = }}$ \dotfill $\bm{{\  mol.L^{-1}}}$
\begin{eqnarray*}
C &=& 78,5\ \textcolor{red}{mmol}.\textcolor{blue}{m^{-3}} \\ 
&=& 78,5\ \textcolor{red}{ 10^{-3}mol}.(\textcolor{blue}{m^{3}})^{-1} \\ 
&=& 78,5\ \textcolor{red}{ 10^{-3}mol}.(\textcolor{blue}{10^{3}L})^{-1} \\ 
&=& 78,5\ \textcolor{red}{ 10^{-3}mol}.\textcolor{blue}{10^{-3}L^{-1}} \\ 
&=& 78,5.10^{-3}.10^{-3}\ mol.L^{-1} \\
&=& 78,5.10^{-6}\ mol.L^{-1} \\ 
\end{eqnarray*}
\item $\bm{{C = 6,5.10^{-2}\ mmol.dm^{-3} = }}$ \dotfill $\bm{{\  mol.m^{-3}}}$
\begin{eqnarray*}
C &=& 6,5.10^{-2}\ \textcolor{red}{mmol}.\textcolor{blue}{dm}^{-3}\\
&=& 6,5.10^{-2}\ \textcolor{red}{10^{-3} mol}.(\textcolor{blue}{10^{-1}m})^{-3}\\
&=& 6,5.10^{-2}.\ \textcolor{red}{10^{-3} mol}.\textcolor{blue}{10^{3}m^{-3}}\\
&=& 6,5.10^{-2}.10^{-3}.10^{3}\ mol.m^{-3}\\
&=& 6,5.10^{-2}\ mol.m^{-3}\\
\end{eqnarray*}
\end{itemize}}
\end{enumerate}			
					\end{solution}}%===  ===%

%Solution 3
					\begin{solution}{}/
لنحسب الموصلية $\sigma$ :
\\لدينا :
$$\sigma = [Na^{+}].\lambda_{Na^{+}} + [NH_4^{+}].\lambda_{NH_4^{+}} + [Cl^-].\lambda_{Cl^-}$$
ولدينا :
\begin{eqnarray*}
[Na^{+}] &=& 2.10^{-3}\ mol.\textcolor{red}{L}^{-1} \\
&=& 2.10^{-3}\ mol.(\textcolor{red}{10^{-3}m^{3}})^{-1} \\
&=& 2.10^{-3}\ mol.\textcolor{red}{10^{3}m^{-3}} \\
&=& 2.10^{-3}.10^{3}\ mol.m^{-3} \\
&=& 2\ mol.m^{-3} \\
\end{eqnarray*}
تذكر أن :
\fbox{$1 \ mol.L^{-1} = \textcolor{red}{10^{3}}\ mol.m^{-3}$}
\\\begin{eqnarray*}
[NH_4^{+}] &=& 3.10^{-3}\ \textcolor{red}{mol.L^{-1}} \\
&=& 3.10^{-3}.\textcolor{red}{10^{3}\ mol.m^{-3}}  \\
&=& 3\ mol.m^{-3}
\end{eqnarray*}
\begin{eqnarray*}
[Cl^{-}] &=& 5.10^{-3}\ \textcolor{red}{mol.L^{-1}} \\
&=& 5.10^{-3}.\textcolor{red}{10^{3}\ mol.m^{-3}}  \\
&=& 5\ mol.m^{-3}
\end{eqnarray*}
إذن :
\begin{eqnarray*}
\sigma &=& [Na^{+}].\lambda_{Na^{+}} + [NH_4^{+}].\lambda_{NH_4^{+}} + [Cl^-].\lambda_{Cl^-} \\
&=& 2\times 5,0.10^{-3} + 3\times 7,4.10^{-3} + 5\times 7,6.10^{-3}\\
&=& (2\times 5+3\times 7,6 +5\times 7,4).10^{-3} \\
&=& 70,2.10^{-3}\ S.m^{-1}
\end{eqnarray*}
	\end{solution}%===  ===%

Exercice.Enonce:
%Exercice 11
					\begin{exercice}{}/				
يستعمل نترات الأمونيوم كثيرا في الفلاحة كسماد، ويتم تحضيره بتمرير غاز الأمونياك في محلول مائي لحمض النتريك حسب المعادلة التالية :
\hfill
$NH_{3(g)} + H_3O^+_{(aq)} \rightarrow NH^+_{4(aq)} + H_2O_{(l)}$
\begin{enumerate}
\vspace{-0.2cm}
\item هل هذا التفاعل تفاعل حمض- قاعدة ؟ علل إجابتك.
\vspace{-0.2cm}
\item ما المزدوجتان المشاركتان في هذا التفاعل؟
\vspace{-0.2cm}
\item يحضر محلول مائي لحمض النتريك بتفاعل حمض النتريك مع الماء . اكتب معادلة هذا التفاعل .
\vspace{-0.2cm}
\item حسب كمية مادة أيونات أوكسونيوم الموجودة في محلول نسبة كتلة حمض النتريك فيه هي
$60\%$.
\vspace{-0.2cm}
\item احسب حجم غاز الأمونياك اللازم لتتفاعل كليا أيونات أوكسونيوم , عند
$20\ ^\circ C$
و تحت الضغط
$1,013.10^{5}\ Pa$.
\end{enumerate}
\textbf{نعطي :}
$M(H)=1\ g.mol^{-1}$ ;
$M(O)=16\ g.mol^{-1}$ ;
$M(N)=14\ g.mol^{-1}$ ;
$V_m = 24\ l.mol^{-1}$ ;
$d=1,37$.
\end{exercice}%=== source ===%

%Exercice 2
					\begin{exercice}{}/
