%Exercice 23
					\begin{exercice}{}/
					يمر في وشيعة 
					$AB$
					 معامل تحريضها 
					$L$
					  تيار كهربائي 
					شدته
					$i=\dfrac{10t}{4+5t}$.
					\begin{enumerate}
					\begin{minipage}{0.6\linewidth}
					\item 
					 عبر عن التوتر 
					$u_{AB}$
					  بدلالة 
					$L $
					   و
					$i(t)$
					\item استنتج قيمة 
					$L$
					 إذا علمت أن
					$u_{AB}(3\ ms) = 1,5\ V$. 
					\end{minipage}
					\begin{minipage}{0.4\linewidth}
					\begin{flushleft}
					\fbox{\includegraphics[width=0.9\linewidth]{images/DeuxiemeBac/Ar/p7/im1.png}}
					\end{flushleft}
					\end{minipage}
					\end{enumerate}
					\end{exercice}%=== source ===%

%Exercice 5
					\begin{exercice}{}/
					يمثل الشكل جانبه تغيرات بدلالة الزمن للسرعة اللحظية
					لمركز قصور جسم صلب في حركة إزاحة مستقيمية .
					\begin{center}					\fbox{\includegraphics[width=0.5\linewidth]{images/DeuxiemeBac/Ar/p12/im2.png}}
										\end{center}
					\begin{enumerate}
					\item  في أي مجال يكون مجموع القوى المطبقة على الجسم
					منعدما ؟ علل جوابك.
					\item
					في أي مجال تكون حركة الجسم غير مستقيمية منتظمة؟
					\end{enumerate}
					\end{exercice}%=== source ===%
